input1 = input1.assignTimestampsAndWatermarks(new AscendingTimestampExtractor<Tuple3<Long, String, String>>() { @Override public long extractAscendingTimestamp(Tuple3<Long, String, String> arg0) { return arg0.f0; } }); DataStream<Tuple2<Long, String>> input2 = ...;
input2 = input2.assignTimestampsAndWatermarks(new AscendingTimestampExtractor<Tuple2<Long, String>>() { @Override public long extractAscendingTimestamp(Tuple2<Long, String> stringStringTuple2) { return stringStringTuple2.f0; }
}); input1.coGroup(input2).where(new KeySelector<Tuple3<Long, String, String>, String>() { @Override public String getKey(Tuple3<Long, String, String> itemEntity) throws Exception { return itemEntity.f1; }
