.apply(new CoGroupFunction<Tuple3<Long, String, String>, Tuple2<Long, String>, String>() { @Override public void coGroup(Iterable<Tuple3<Long, String, String>> first, Iterable<Tuple2<Long, String>> second, Collector<String> collector) throws Exception { StringBuilder buffer = new StringBuilder(); buffer.append("DataStream first:\n"); for (Tuple3<Long, String, String> value : first) { buffer.append(value).append("\n"); } buffer.append("DataStream second:\n"); for (Tuple2<Long, String> value : second) { buffer.append(value.f0).append("=>").append(value.f1).append("\n"); } collector.collect(buffer.toString()); }
